mapper.select:
SELECT um.user_id ,um.user_nm ,um.birth_enc ,um.tel_num_enc ,um.email ,um.dept_nm ,um.weight ,um.height ,um.gender ,um.blood_type ,um.user_profile ,(SELECT ARRAY_TO_STRING(ARRAY_AGG((SELECT concat(user_nm,' ',tel_num_enc) FROM user_mng WHERE user_seq = guardian_seq)),', ') FROM guardian_mapping_info gi WHERE gi.user_seq_fk = um.user_seq) AS guardian_nm ,(select cast(array_to_json(array_agg(a)) as varchar) from (select gi.guardian_seq , gi.guardian_id , umg.user_nm , umg.tel_num_enc FROM guardian_mapping_info gi left join user_mng umg on gi.guardian_seq = umg.user_seq WHERE gi.user_seq_fk = um.user_seq) a) as guardian ,(select user_nm FROM user_mng WHERE di.doctor_seq = user_seq ) AS doctor_nm ,(select dept_nm FROM user_mng WHERE di.doctor_seq = user_seq ) AS doctor_dept ,(select doctor_seq FROM user_mng WHERE di.doctor_seq = user_seq ) AS doctor_seq ,(select user_id FROM user_mng WHERE di.doctor_seq = user_seq ) AS doctor_id FROM user_mng um LEFT JOIN doctor_mapping_info di ON di.user_seq_fk = um.user_seq LEFT JOIN USER_AUTH_INFO ua on ua.USER_SEQ = um.USER_SEQ
and ua.user_role_fk = #{userRoleFk} and um.user_id = #{userId}
and um.user_pw_enc = #{userPwEnc}

mapper.delete:
delete from guardian_mapping_info where user_seq_fk = (select user_seq from user_mng um where um.user_id = #{userId})
and guardian_seq not in
#{item}

mapper.update:
UPDATE user_mng
SET upt_dt = now()
, upt_id = #{userId}
, user_nm = #{userNm}
, email = #{email}
, tel_num_enc = #{telNumEnc}
, birth_enc = #{birthEnc}

    , dept_nm = #{deptNm}

, height = #{height}
, weight = #{weight}
, gender = #{gender}
, blood_type = #{bloodType}
WHERE user_id = #{userId}

UPDATE user_mng
SET upt_dt = now()
  ,upt_id = #{userId}
  ,user_pw_enc = #{newUserPwEnc}
WHERE user_id = #{userId}
  and user_pw_enc = #{userPwEnc}

mapper.insert:

    select user_seq from user_mng um where um.user_id = #{userId}

INSERT INTO healthcare.doctor_mapping_info
(user_seq_fk, user_id_fk, doctor_seq, doctor_id, reg_id)
VALUES(#{userSeq}, #{userId}, #{doctorSeq}, #{doctorId}, #{userId})
ON CONFLICT (user_seq_fk, doctor_seq)
DO UPDATE
SET
doctor_seq = #{doctorSeq}
, doctor_id = #{doctorId}
, upt_dt = now()
, upt_id = #{userId}
